// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";

contract FakeToken is ERC20 {
    constructor(uint256 initialSupply) ERC20("FakeToken", "FTK") {
        _mint(msg.sender, initialSupply);
    }
}

contracts/FarmerGovt.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

/**
 * @title FarmerGovt
 * @dev Smart contract for managing transactions between farmers and government
 * @author SIH Team
 */
contract FarmerGovt {
    // Events
    event TransactionRecorded(
        bytes32 indexed transactionId,
        address indexed farmerId,
        address indexed cropId,
        uint256 quantity,
        uint256 pricePerUnit,
        uint256 totalAmount,
        address govtEmployeeId,
        string verificationHash,
        uint256 timestamp
    );

    event PaymentProcessed(
        bytes32 indexed transactionId,
        address indexed farmerId,
        uint256 amount,
        string paymentMethod,
        string paymentReference,
        uint256 timestamp
    );

    event VerificationUpdated(
        address indexed cropId,
        string qualityGrade,
        uint256 qualityScore,
        string verificationHash,
        uint256 timestamp
    );

    // Structs
    struct Transaction {
        bytes32 transactionId;
        address farmerId;
        address cropId;
        uint256 quantity;
        uint256 pricePerUnit;
        uint256 totalAmount;
        address govtEmployeeId;
        string verificationHash;
        uint256 timestamp;
        bool isPaid;
        string paymentReference;
    }

    struct CropVerification {
        address cropId;
        string qualityGrade;
        uint256 qualityScore;
        string verificationHash;
        address verifiedBy;
        uint256 timestamp;
        bool isVerified;
    }

    // State variables
    mapping(bytes32 => Transaction) public transactions;
    mapping(address => CropVerification) public cropVerifications;
    mapping(address => uint256) public farmerEarnings;
    mapping(address => uint256) public farmerTransactionCount;

    address public owner;
    address public treasury;
    uint256 public platformFeePercentage; // in basis points (100 = 1%)
    uint256 public totalTransactions;
    uint256 public totalVolume;

    // Modifiers
    modifier onlyOwner() {
        require(msg.sender == owner, "Only owner can call this function");
        _;
    }

    modifier onlyGovtEmployee() {
        // In a real implementation, this would check against a whitelist of government employees
        require(msg.sender != address(0), "Invalid government employee");
        _;
    }

    // Constructor
    constructor(address _treasury, uint256 _platformFeePercentage) {
        owner = msg.sender;
        treasury = _treasury;
        platformFeePercentage = _platformFeePercentage;
    }

    /**
     * @dev Record a transaction between farmer and government
     * @param _farmerId Address of the farmer
     * @param _cropId Address of the crop
     * @param _quantity Quantity of the crop
     * @param _pricePerUnit Price per unit
     * @param _totalAmount Total amount
     * @param _govtEmployeeId Address of the government employee
     * @param _verificationHash Hash of the verification data
     */
    function recordTransaction(
        address _farmerId,
        address _cropId,
        uint256 _quantity,
        uint256 _pricePerUnit,
        uint256 _totalAmount,
        address _govtEmployeeId,
        string memory _verificationHash
    ) external onlyGovtEmployee {
        require(_farmerId != address(0), "Invalid farmer address");
        require(_cropId != address(0), "Invalid crop address");
        require(_quantity > 0, "Quantity must be greater than 0");
        require(_pricePerUnit > 0, "Price per unit must be greater than 0");
        require(_totalAmount > 0, "Total amount must be greater than 0");

        bytes32 transactionId = keccak256(
            abi.encodePacked(
                _farmerId,
                _cropId,
                _quantity,
                _pricePerUnit,
                block.timestamp,
                msg.sender
            )
        );

        require(transactions[transactionId].transactionId == bytes32(0), "Transaction already exists");

        Transaction memory newTransaction = Transaction({
            transactionId: transactionId,
            farmerId: _farmerId,
            cropId: _cropId,
            quantity: _quantity,
            pricePerUnit: _pricePerUnit,
            totalAmount: _totalAmount,
            govtEmployeeId: _govtEmployeeId,
            verificationHash: _verificationHash,
            timestamp: block.timestamp,
            isPaid: false,
            paymentReference: ""
        });

        transactions[transactionId] = newTransaction;
        totalTransactions++;
        totalVolume += _totalAmount;

        emit TransactionRecorded(
            transactionId,
            _farmerId,
            _cropId,
            _quantity,
            _pricePerUnit,
            _totalAmount,
            _govtEmployeeId,
            _verificationHash,
            block.timestamp
        );
    }

    /**
     * @dev Process payment to farmer
     * @param _transactionId Transaction ID
     * @param _paymentMethod Payment method used
     * @param _paymentReference Payment reference
     */
    function processPayment(
        bytes32 _transactionId,
        string memory _paymentMethod,
        string memory _paymentReference
    ) external onlyGovtEmployee {
        require(transactions[_transactionId].transactionId != bytes32(0), "Transaction not found");
        require(!transactions[_transactionId].isPaid, "Payment already processed");

        Transaction storage transaction = transactions[_transactionId];
        
        // Calculate platform fee
        uint256 platformFee = (transaction.totalAmount * platformFeePercentage) / 10000;
        uint256 netAmount = transaction.totalAmount - platformFee;

        // Update farmer earnings
        farmerEarnings[transaction.farmerId] += netAmount;
        farmerTransactionCount[transaction.farmerId]++;

        // Mark transaction as paid
        transaction.isPaid = true;
        transaction.paymentReference = _paymentReference;

        emit PaymentProcessed(
            _transactionId,
            transaction.farmerId,
            netAmount,
            _paymentMethod,
            _paymentReference,
            block.timestamp
        );
    }

    /**
     * @dev Update crop verification
     * @param _cropId Address of the crop
     * @param _qualityGrade Quality grade
     * @param _qualityScore Quality score
     * @param _verificationHash Hash of the verification data
     */
    function updateCropVerification(
        address _cropId,
        string memory _qualityGrade,
        uint256 _qualityScore,
        string memory _verificationHash
    ) external onlyGovtEmployee {
        require(_cropId != address(0), "Invalid crop address");
        require(_qualityScore >= 0 && _qualityScore <= 100, "Quality score must be between 0 and 100");

        CropVerification storage verification = cropVerifications[_cropId];
        verification.cropId = _cropId;
        verification.qualityGrade = _qualityGrade;
        verification.qualityScore = _qualityScore;
        verification.verificationHash = _verificationHash;
        verification.verifiedBy = msg.sender;
        verification.timestamp = block.timestamp;
        verification.isVerified = true;

        emit VerificationUpdated(
            _cropId,
            _qualityGrade,
            _qualityScore,
            _verificationHash,
            block.timestamp
        );
    }

    /**
     * @dev Get transaction details
     * @param _transactionId Transaction ID
     * @return Transaction details
     */
    function getTransaction(bytes32 _transactionId) external view returns (Transaction memory) {
        require(transactions[_transactionId].transactionId != bytes32(0), "Transaction not found");
        return transactions[_transactionId];
    }

    /**
     * @dev Get crop verification details
     * @param _cropId Crop address
     * @return Crop verification details
     */
    function getCropVerification(address _cropId) external view returns (CropVerification memory) {
        require(cropVerifications[_cropId].cropId != address(0), "Crop verification not found");
        return cropVerifications[_cropId];
    }

    /**
     * @dev Get farmer statistics
     * @param _farmerId Farmer address
     * @return Total earnings and transaction count
     */
    function getFarmerStats(address _farmerId) external view returns (uint256, uint256) {
        return (farmerEarnings[_farmerId], farmerTransactionCount[_farmerId]);
    }

    /**
     * @dev Get contract statistics
     * @return Total transactions and volume
     */
    function getContractStats() external view returns (uint256, uint256) {
        return (totalTransactions, totalVolume);
    }

    /**
     * @dev Update platform fee percentage (only owner)
     * @param _newFeePercentage New fee percentage in basis points
     */
    function updatePlatformFee(uint256 _newFeePercentage) external onlyOwner {
        require(_newFeePercentage <= 1000, "Fee percentage cannot exceed 10%");
        platformFeePercentage = _newFeePercentage;
    }

    /**
     * @dev Update treasury address (only owner)
     * @param _newTreasury New treasury address
     */
    function updateTreasury(address _newTreasury) external onlyOwner {
        require(_newTreasury != address(0), "Invalid treasury address");
        treasury = _newTreasury;
    }

    /**
     * @dev Transfer ownership (only owner)
     * @param _newOwner New owner address
     */
    function transferOwnership(address _newOwner) external onlyOwner {
        require(_newOwner != address(0), "Invalid new owner address");
        owner = _newOwner;
    }

    /**
     * @dev Emergency pause function (only owner)
     */
    function pause() external onlyOwner {
        // In a real implementation, this would pause the contract
        // For now, we'll just emit an event
        emit TransactionRecorded(
            bytes32(0),
            address(0),
            address(0),
            0,
            0,
            0,
            address(0),
            "CONTRACT_PAUSED",
            block.timestamp
        );
    }

    /**
     * @dev Withdraw funds to treasury (only owner)
     * @param _amount Amount to withdraw
     */
    function withdrawToTreasury(uint256 _amount) external onlyOwner {
        require(_amount <= address(this).balance, "Insufficient contract balance");
        require(treasury != address(0), "Treasury address not set");
        
        payable(treasury).transfer(_amount);
    }

    /**
     * @dev Receive function to accept ETH
     */
    receive() external payable {
        // Contract can receive ETH
    }

    /**
     * @dev Fallback function
     */
    fallback() external payable {
        // Fallback function
    }
}

contracts/GovtCustomer.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

/**
 * @title GovtCustomer
 * @dev Smart contract for managing transactions between government and customers
 * @author SIH Team
 */
contract GovtCustomer {
    // Events
    event SaleRecorded(
        bytes32 indexed saleId,
        address indexed customerId,
        address indexed cropId,
        uint256 quantity,
        uint256 pricePerUnit,
        uint256 totalAmount,
        address govtEmployeeId,
        string paymentHash,
        uint256 timestamp
    );

    event PaymentReceived(
        bytes32 indexed saleId,
        address indexed customerId,
        uint256 amount,
        string paymentMethod,
        string paymentReference,
        uint256 timestamp
    );

    event CustomerRegistered(
        address indexed customerId,
        string customerCode,
        string name,
        uint256 timestamp
    );

    // Structs
    struct Sale {
        bytes32 saleId;
        address customerId;
        address cropId;
        uint256 quantity;
        uint256 pricePerUnit;
        uint256 totalAmount;
        address govtEmployeeId;
        string paymentHash;
        uint256 timestamp;
        bool isPaid;
        string paymentReference;
        string deliveryAddress;
        string deliveryStatus;
    }

    struct Customer {
        address customerId;
        string customerCode;
        string name;
        string email;
        string phone;
        address walletAddress;
        uint256 totalPurchases;
        uint256 totalSpent;
        uint256 loyaltyPoints;
        bool isActive;
        uint256 registrationTimestamp;
    }

    // State variables
    mapping(bytes32 => Sale) public sales;
    mapping(address => Customer) public customers;
    mapping(address => uint256) public customerPurchaseCount;
    mapping(address => uint256) public customerSpending;

    address public owner;
    address public treasury;
    uint256 public platformFeePercentage; // in basis points (100 = 1%)
    uint256 public totalSales;
    uint256 public totalRevenue;
    uint256 public loyaltyPointsRate; // points per rupee spent

    // Modifiers
    modifier onlyOwner() {
        require(msg.sender == owner, "Only owner can call this function");
        _;
    }

    modifier onlyGovtEmployee() {
        // In a real implementation, this would check against a whitelist of government employees
        require(msg.sender != address(0), "Invalid government employee");
        _;
    }

    modifier onlyRegisteredCustomer() {
        require(customers[msg.sender].isActive, "Customer not registered or inactive");
        _;
    }

    // Constructor
    constructor(address _treasury, uint256 _platformFeePercentage, uint256 _loyaltyPointsRate) {
        owner = msg.sender;
        treasury = _treasury;
        platformFeePercentage = _platformFeePercentage;
        loyaltyPointsRate = _loyaltyPointsRate;
    }

    /**
     * @dev Register a new customer
     * @param _customerCode Customer code
     * @param _name Customer name
     * @param _email Customer email
     * @param _phone Customer phone
     * @param _walletAddress Customer wallet address
     */
    function registerCustomer(
        string memory _customerCode,
        string memory _name,
        string memory _email,
        string memory _phone,
        address _walletAddress
    ) external {
        require(customers[msg.sender].customerId == address(0), "Customer already registered");
        require(bytes(_customerCode).length > 0, "Customer code cannot be empty");
        require(bytes(_name).length > 0, "Name cannot be empty");
        require(_walletAddress != address(0), "Invalid wallet address");

        Customer memory newCustomer = Customer({
            customerId: msg.sender,
            customerCode: _customerCode,
            name: _name,
            email: _email,
            phone: _phone,
            walletAddress: _walletAddress,
            totalPurchases: 0,
            totalSpent: 0,
            loyaltyPoints: 0,
            isActive: true,
            registrationTimestamp: block.timestamp
        });

        customers[msg.sender] = newCustomer;

        emit CustomerRegistered(
            msg.sender,
            _customerCode,
            _name,
            block.timestamp
        );
    }

    /**
     * @dev Record a sale to customer
     * @param _customerId Address of the customer
     * @param _cropId Address of the crop
     * @param _quantity Quantity of the crop
     * @param _pricePerUnit Price per unit
     * @param _totalAmount Total amount
     * @param _govtEmployeeId Address of the government employee
     * @param _paymentHash Hash of the payment data
     * @param _deliveryAddress Delivery address
     */
    function recordSale(
        address _customerId,
        address _cropId,
        uint256 _quantity,
        uint256 _pricePerUnit,
        uint256 _totalAmount,
        address _govtEmployeeId,
        string memory _paymentHash,
        string memory _deliveryAddress
    ) external onlyGovtEmployee {
        require(_customerId != address(0), "Invalid customer address");
        require(_cropId != address(0), "Invalid crop address");
        require(customers[_customerId].isActive, "Customer not registered or inactive");
        require(_quantity > 0, "Quantity must be greater than 0");
        require(_pricePerUnit > 0, "Price per unit must be greater than 0");
        require(_totalAmount > 0, "Total amount must be greater than 0");

        bytes32 saleId = keccak256(
            abi.encodePacked(
                _customerId,
                _cropId,
                _quantity,
                _pricePerUnit,
                block.timestamp,
                msg.sender
            )
        );

        require(sales[saleId].saleId == bytes32(0), "Sale already exists");

        Sale memory newSale = Sale({
            saleId: saleId,
            customerId: _customerId,
            cropId: _cropId,
            quantity: _quantity,
            pricePerUnit: _pricePerUnit,
            totalAmount: _totalAmount,
            govtEmployeeId: _govtEmployeeId,
            paymentHash: _paymentHash,
            timestamp: block.timestamp,
            isPaid: false,
            paymentReference: "",
            deliveryAddress: _deliveryAddress,
            deliveryStatus: "PENDING"
        });

        sales[saleId] = newSale;
        totalSales++;
        totalRevenue += _totalAmount;

        emit SaleRecorded(
            saleId,
            _customerId,
            _cropId,
            _quantity,
            _pricePerUnit,
            _totalAmount,
            _govtEmployeeId,
            _paymentHash,
            block.timestamp
        );
    }

    /**
     * @dev Process customer payment
     * @param _saleId Sale ID
     * @param _paymentMethod Payment method used
     * @param _paymentReference Payment reference
     */
    function processCustomerPayment(
        bytes32 _saleId,
        string memory _paymentMethod,
        string memory _paymentReference
    ) external onlyGovtEmployee {
        require(sales[_saleId].saleId != bytes32(0), "Sale not found");
        require(!sales[_saleId].isPaid, "Payment already processed");

        Sale storage sale = sales[_saleId];
        
        // Calculate platform fee
        uint256 platformFee = (sale.totalAmount * platformFeePercentage) / 10000;
        uint256 netAmount = sale.totalAmount - platformFee;

        // Update customer statistics
        customers[sale.customerId].totalPurchases++;
        customers[sale.customerId].totalSpent += sale.totalAmount;
        customers[sale.customerId].loyaltyPoints += (sale.totalAmount * loyaltyPointsRate) / 100;
        
        customerPurchaseCount[sale.customerId]++;
        customerSpending[sale.customerId] += sale.totalAmount;

        // Mark sale as paid
        sale.isPaid = true;
        sale.paymentReference = _paymentReference;

        emit PaymentReceived(
            _saleId,
            sale.customerId,
            netAmount,
            _paymentMethod,
            _paymentReference,
            block.timestamp
        );
    }

    /**
     * @dev Update delivery status
     * @param _saleId Sale ID
     * @param _deliveryStatus New delivery status
     */
    function updateDeliveryStatus(
        bytes32 _saleId,
        string memory _deliveryStatus
    ) external onlyGovtEmployee {
        require(sales[_saleId].saleId != bytes32(0), "Sale not found");
        require(sales[_saleId].isPaid, "Sale not paid yet");

        sales[_saleId].deliveryStatus = _deliveryStatus;
    }

    /**
     * @dev Get sale details
     * @param _saleId Sale ID
     * @return Sale details
     */
    function getSale(bytes32 _saleId) external view returns (Sale memory) {
        require(sales[_saleId].saleId != bytes32(0), "Sale not found");
        return sales[_saleId];
    }

    /**
     * @dev Get customer details
     * @param _customerId Customer address
     * @return Customer details
     */
    function getCustomer(address _customerId) external view returns (Customer memory) {
        require(customers[_customerId].customerId != address(0), "Customer not found");
        return customers[_customerId];
    }

    /**
     * @dev Get customer statistics
     * @param _customerId Customer address
     * @return Total purchases, spending, and loyalty points
     */
    function getCustomerStats(address _customerId) external view returns (uint256, uint256, uint256) {
        return (
            customers[_customerId].totalPurchases,
            customers[_customerId].totalSpent,
            customers[_customerId].loyaltyPoints
        );
    }

    /**
     * @dev Get contract statistics
     * @return Total sales and revenue
     */
    function getContractStats() external view returns (uint256, uint256) {
        return (totalSales, totalRevenue);
    }

    /**
     * @dev Redeem loyalty points
     * @param _customerId Customer address
     * @param _points Points to redeem
     * @param _discountAmount Discount amount
     */
    function redeemLoyaltyPoints(
        address _customerId,
        uint256 _points,
        uint256 _discountAmount
    ) external onlyGovtEmployee {
        require(customers[_customerId].isActive, "Customer not active");
        require(customers[_customerId].loyaltyPoints >= _points, "Insufficient loyalty points");
        require(_discountAmount > 0, "Discount amount must be greater than 0");

        customers[_customerId].loyaltyPoints -= _points;
    }

    /**
     * @dev Update platform fee percentage (only owner)
     * @param _newFeePercentage New fee percentage in basis points
     */
    function updatePlatformFee(uint256 _newFeePercentage) external onlyOwner {
        require(_newFeePercentage <= 1000, "Fee percentage cannot exceed 10%");
        platformFeePercentage = _newFeePercentage;
    }

    /**
     * @dev Update loyalty points rate (only owner)
     * @param _newRate New loyalty points rate
     */
    function updateLoyaltyPointsRate(uint256 _newRate) external onlyOwner {
        require(_newRate > 0, "Loyalty points rate must be greater than 0");
        loyaltyPointsRate = _newRate;
    }

    /**
     * @dev Update treasury address (only owner)
     * @param _newTreasury New treasury address
     */
    function updateTreasury(address _newTreasury) external onlyOwner {
        require(_newTreasury != address(0), "Invalid treasury address");
        treasury = _newTreasury;
    }

    /**
     * @dev Transfer ownership (only owner)
     * @param _newOwner New owner address
     */
    function transferOwnership(address _newOwner) external onlyOwner {
        require(_newOwner != address(0), "Invalid new owner address");
        owner = _newOwner;
    }

    /**
     * @dev Withdraw funds to treasury (only owner)
     * @param _amount Amount to withdraw
     */
    function withdrawToTreasury(uint256 _amount) external onlyOwner {
        require(_amount <= address(this).balance, "Insufficient contract balance");
        require(treasury != address(0), "Treasury address not set");
        
        payable(treasury).transfer(_amount);
    }

    /**
     * @dev Receive function to accept ETH
     */
    receive() external payable {
        // Contract can receive ETH
    }

    /**
     * @dev Fallback function
     */
    fallback() external payable {
        // Fallback function
    }
}

contracts/PaymentContract.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

/**
 * @title PaymentContract
 * @dev Smart contract for handling agricultural supply chain payments via MetaMask
 * @author SIH Team
 */
contract PaymentContract {
    // Events
    event PaymentInitiated(
        bytes32 indexed paymentId,
        address indexed payer,
        address indexed payee,
        uint256 amount,
        string transactionType,
        uint256 timestamp
    );

    event PaymentCompleted(
        bytes32 indexed paymentId,
        address indexed payer,
        address indexed payee,
        uint256 amount,
        string transactionHash,
        uint256 timestamp
    );

    event PaymentFailed(
        bytes32 indexed paymentId,
        address indexed payer,
        string reason,
        uint256 timestamp
    );

    event RefundProcessed(
        bytes32 indexed paymentId,
        address indexed originalPayer,
        address indexed originalPayee,
        uint256 refundAmount,
        string reason,
        uint256 timestamp
    );

    // Structs
    struct Payment {
        bytes32 paymentId;
        address payer;
        address payee;
        uint256 amount;
        string transactionType; // "FARMER_PAYMENT", "CUSTOMER_PAYMENT", "PLATFORM_FEE"
        string status; // "PENDING", "COMPLETED", "FAILED", "REFUNDED"
        string transactionHash;
        uint256 timestamp;
        string metadata; // JSON string with additional data
        bool isActive;
    }

    struct PlatformFee {
        uint256 percentage; // Basis points (e.g., 250 = 2.5%)
        address treasury;
        uint256 totalCollected;
    }

    // State variables
    mapping(bytes32 => Payment) public payments;
    mapping(address => uint256) public userBalances;
    mapping(address => uint256) public userTotalPaid;
    mapping(address => uint256) public userTotalReceived;

    PlatformFee public platformFee;
    address public owner;
    address public traceabilityContract;

    uint256 public totalPayments;
    uint256 public totalVolume;
    uint256 public totalPlatformFees;

    // Modifiers
    modifier onlyOwner() {
        require(msg.sender == owner, "Only owner can call this function");
        _;
    }

    modifier onlyAuthorizedContract() {
        require(
            msg.sender == traceabilityContract || msg.sender == owner,
            "Only authorized contracts can call this function"
        );
        _;
    }

    modifier validPayment(bytes32 _paymentId) {
        require(payments[_paymentId].paymentId != bytes32(0), "Payment does not exist");
        _;
    }

    // Constructor
    constructor(address _traceabilityContract) {
        owner = msg.sender;
        traceabilityContract = _traceabilityContract;
        platformFee = PlatformFee({
            percentage: 250, // 2.5%
            treasury: msg.sender,
            totalCollected: 0
        });
    }

    /**
     * @dev Initiate a payment
     * @param _paymentId Unique payment identifier
     * @param _payee Address of the recipient
     * @param _amount Amount to be paid (in wei)
     * @param _transactionType Type of transaction
     * @param _metadata Additional payment metadata
     */
    function initiatePayment(
        bytes32 _paymentId,
        address _payee,
        uint256 _amount,
        string memory _transactionType,
        string memory _metadata
    ) external payable {
        require(_payee != address(0), "Invalid payee address");
        require(_amount > 0, "Amount must be greater than zero");
        require(msg.value >= _amount, "Insufficient payment amount");
        require(payments[_paymentId].paymentId == bytes32(0), "Payment ID already exists");

        // Create payment record
        Payment memory newPayment = Payment({
            paymentId: _paymentId,
            payer: msg.sender,
            payee: _payee,
            amount: _amount,
            transactionType: _transactionType,
            status: "PENDING",
            transactionHash: "",
            timestamp: block.timestamp,
            metadata: _metadata,
            isActive: true
        });

        payments[_paymentId] = newPayment;
        totalPayments++;

        emit PaymentInitiated(
            _paymentId,
            msg.sender,
            _payee,
            _amount,
            _transactionType,
            block.timestamp
        );
    }

    /**
     * @dev Complete a payment
     * @param _paymentId Payment identifier
     * @param _transactionHash Blockchain transaction hash
     */
    function completePayment(
        bytes32 _paymentId,
        string memory _transactionHash
    ) external validPayment(_paymentId) {
        Payment storage payment = payments[_paymentId];
        require(
            keccak256(abi.encodePacked(payment.status)) == keccak256(abi.encodePacked("PENDING")),
            "Payment not in pending status"
        );
        require(payment.payer == msg.sender, "Only payer can complete payment");

        // Calculate platform fee
        uint256 feeAmount = (payment.amount * platformFee.percentage) / 10000;
        uint256 netAmount = payment.amount - feeAmount;

        // Transfer funds
        payable(payment.payee).transfer(netAmount);
        payable(platformFee.treasury).transfer(feeAmount);

        // Update payment status
        payment.status = "COMPLETED";
        payment.transactionHash = _transactionHash;

        // Update balances and statistics
        userBalances[payment.payee] += netAmount;
        userTotalPaid[payment.payer] += payment.amount;
        userTotalReceived[payment.payee] += netAmount;
        totalVolume += payment.amount;
        totalPlatformFees += feeAmount;
        platformFee.totalCollected += feeAmount;

        emit PaymentCompleted(
            _paymentId,
            payment.payer,
            payment.payee,
            payment.amount,
            _transactionHash,
            block.timestamp
        );
    }

    /**
     * @dev Process payment failure
     * @param _paymentId Payment identifier
     * @param _reason Reason for failure
     */
    function processPaymentFailure(
        bytes32 _paymentId,
        string memory _reason
    ) external onlyAuthorizedContract validPayment(_paymentId) {
        Payment storage payment = payments[_paymentId];
        require(
            keccak256(abi.encodePacked(payment.status)) == keccak256(abi.encodePacked("PENDING")),
            "Payment not in pending status"
        );

        // Refund to payer
        payable(payment.payer).transfer(payment.amount);

        // Update payment status
        payment.status = "FAILED";

        emit PaymentFailed(_paymentId, payment.payer, _reason, block.timestamp);
    }

    /**
     * @dev Process refund
     * @param _paymentId Payment identifier
     * @param _refundAmount Amount to refund
     * @param _reason Reason for refund
     */
    function processRefund(
        bytes32 _paymentId,
        uint256 _refundAmount,
        string memory _reason
    ) external onlyAuthorizedContract validPayment(_paymentId) {
        Payment storage payment = payments[_paymentId];
        require(
            keccak256(abi.encodePacked(payment.status)) == keccak256(abi.encodePacked("COMPLETED")),
            "Payment not completed"
        );
        require(_refundAmount <= payment.amount, "Refund amount exceeds payment amount");

        // Transfer refund to original payer
        payable(payment.payer).transfer(_refundAmount);

        // Update payment status
        payment.status = "REFUNDED";

        // Update balances
        userBalances[payment.payee] -= _refundAmount;
        userTotalPaid[payment.payer] -= _refundAmount;
        userTotalReceived[payment.payee] -= _refundAmount;

        emit RefundProcessed(
            _paymentId,
            payment.payer,
            payment.payee,
            _refundAmount,
            _reason,
            block.timestamp
        );
    }

    /**
     * @dev Get payment details
     * @param _paymentId Payment identifier
     * @return Payment details
     */
    function getPayment(bytes32 _paymentId) external view returns (Payment memory) {
        require(payments[_paymentId].paymentId != bytes32(0), "Payment does not exist");
        return payments[_paymentId];
    }

    /**
     * @dev Get user payment statistics
     * @param _user User address
     * @return totalPaid, totalReceived, balance
     */
    function getUserStats(address _user) external view returns (uint256, uint256, uint256) {
        return (
            userTotalPaid[_user],
            userTotalReceived[_user],
            userBalances[_user]
        );
    }

    /**
     * @dev Get platform statistics
     * @return totalPayments, totalVolume, totalFees
     */
    function getPlatformStats() external view returns (uint256, uint256, uint256) {
        return (totalPayments, totalVolume, totalPlatformFees);
    }

    /**
     * @dev Update platform fee (only owner)
     * @param _percentage New fee percentage in basis points
     * @param _treasury New treasury address
     */
    function updatePlatformFee(uint256 _percentage, address _treasury) external onlyOwner {
        require(_percentage <= 1000, "Fee cannot exceed 10%");
        require(_treasury != address(0), "Invalid treasury address");

        platformFee.percentage = _percentage;
        platformFee.treasury = _treasury;
    }

    /**
     * @dev Update traceability contract address
     * @param _traceabilityContract New traceability contract address
     */
    function updateTraceabilityContract(address _traceabilityContract) external onlyOwner {
        require(_traceabilityContract != address(0), "Invalid contract address");
        traceabilityContract = _traceabilityContract;
    }

    /**
     * @dev Withdraw contract balance (only owner)
     * @param _amount Amount to withdraw
     */
    function withdrawBalance(uint256 _amount) external onlyOwner {
        require(_amount <= address(this).balance, "Insufficient contract balance");
        payable(owner).transfer(_amount);
    }

    /**
     * @dev Emergency pause (only owner)
     * @param _paymentId Payment to pause
     */
    function pausePayment(bytes32 _paymentId) external onlyOwner validPayment(_paymentId) {
        payments[_paymentId].isActive = false;
    }

    /**
     * @dev Resume payment (only owner)
     * @param _paymentId Payment to resume
     */
    function resumePayment(bytes32 _paymentId) external onlyOwner validPayment(_paymentId) {
        payments[_paymentId].isActive = true;
    }

    /**
     * @dev Get contract balance
     * @return Contract balance in wei
     */
    function getContractBalance() external view returns (uint256) {
        return address(this).balance;
    }

    /**
     * @dev Generate payment ID from transaction data
     * @param _transactionId Transaction ID
     * @param _payer Payer address
     * @param _timestamp Timestamp
     * @return Generated payment ID
     */
    function generatePaymentId(
        string memory _transactionId,
        address _payer,
        uint256 _timestamp
    ) external pure returns (bytes32) {
        return keccak256(abi.encodePacked(_transactionId, _payer, _timestamp));
    }

    // Fallback function to receive ETH
    receive() external payable {
        // Allow contract to receive ETH
    }
}

